example.programlisting:
/* set up message area */
message_area = gtk_message_area_new ();
gtk_widget_set_no_show_all (message_area, TRUE);
message_label = gtk_label_new ("");
gtk_widget_show (message_label);
gtk_message_area_set_contents (GTK_MESSAGE_AREA (message_area),
                               message_label);
gtk_message_area_add_button (GTK_MESSAGE_AREA (message_area),
                             GTK_STOCK_OK, GTK_RESPONSE_OK);
g_signal_connect (message_area, "response",
                  G_CALLBACK (gtk_widget_hide), NULL);
gtk_table_attach (GTK_TABLE (table),
                  message_area,
                  0, 1, 2, 3,
                  GTK_EXPAND | GTK_FILL,  0,
                  0,                      0);

/* ... */

/* show an error message */
gtk_label_set_text (GTK_LABEL (message_label), error_message);
gtk_message_area_set_message_type (GTK_MESSAGE_AREA (message_area),
                                   GTK_MESSAGE_ERROR);
gtk_widget_show (message_area);

